import './App.css';
import { Component } from 'react';
import { Subject } from './Subject';
import { ReadContent } from './ReadContent';
import { TOC } from './TOC';
import { Control } from './Control';
import { CreateContent } from './CreateContent';
import {UpdateContent} from './UpdateContent';
class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      subject: { title: 'WEB', sub: 'world wide web' },
      contents: [
        { id: 1, title: 'HTML', desc: 'HTML is for information.' },
        { id: 2, title: 'CSS', desc: 'CSS is for design.' },
        { id: 3, title: 'JavaScript', desc: 'JavaScript is for interactive.' }
      ],
      mode: 'welcome',
      welcome: { title: 'welcome', desc: 'Hello, React' }
    };
  }

  //조회와 수정 컴포넌트가 공통으로 사용하기 위해서(여러번 호출되니까)
  findContentById() {

    //선택된 요소(html, css,js)의 id에 해당하는 객체 찾기  
    let content;
    for (let i = 0; i < this.state.contents.length; i++) {
      if (this.state.id === this.state.contents[i].id) {
        content = this.state.contents[i];

        break;
      }
    };
    return content;
  }
  render() {
    let title, desc;
    let article;
    if (this.state.mode === 'welcome') {
      title = this.state.welcome.title;
      desc = this.state.welcome.desc;

    } else if(this.state.mode === 'update') {
      const content =
      this.findContentById(this.state.selected_content_id);
      title = content.title;
      desc = content.desc;
      article = <UpdateContent
      title={title} desc={desc}></UpdateContent> 

    }else if(this.state.mode === 'read'){
      const content = this.findContentById();
      title = content.title;
      desc = content.desc;

    }else if (this.state.mode === 'create') {
      article = <CreateContent
        onSubmit={function (title, desc) {
          console.log(title, desc);
          this.state.contents.push({
            id: this.state.contents.length + 1,
            title: title,
            desc: desc
          });
          this.setState({
            contents: this.state.contents
          })
        }.bind(this)} />
      }
      return (
        <div className="App">
          <Subject

            title={this.state.subject.title}
            sub={this.state.subject.sub}></Subject>

          <TOC onChangePage={
            (value) => {
              this.setState({ id: value, mode : 'read' });
            }
          } contents={this.state.contents}></TOC>

          <Control onChangeMode={function (mode) {
            this.setState({ mode: mode });
          }.bind(this)} />

          {article}

          {/* 조회와 수정 컴포넌트에서 title desc */}
          <ReadContent title={title} desc={desc}></ReadContent>
        </div>
      );

  }
}


export default App;
